const validateEmail=a=>a.match(/^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/),validate=()=>{const a=$("#result"),t=$("#email").val();return a.text(""),validateEmail(t)?(a.text("adress is valid :)"),a.css("color","green")):(a.text(t+" is not valid :("),a.css("color","red")),!1};$("#email").on("input",validate);